
public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		

	      for (int row = 1; row <= 8; row++) { // row
	       
	            System.out.print(row);
	            if (row == 5) break; // this will break our or the loop
	         }
	         System.out.println();
	      }
	

	
	}
	
	
	


